/**
 * Asserts an {} is an objet.
 * @param name - The property name
 * @param data - The data to validate.
 */
export function check(name: string, data: {}): void

/**
 * Asserts an {} is not null.
 * @param name - The property name
 * @param data - The data to validate.
 */
export function checkIsNotNull(name: string, data: {}): void